mapper.select:
SELECT DISTINCT(AGE.AGENT_ID), AGE.AGENT_NAME, AGE.AGENT_STATUS, AGE.CREATE_DATE, AGE.ERP_CODE, AGE.IS_DELETE, AGE.IS_VIRTUAL, AGE.JOIN_DATE, AGE.PLAN_DATE FROM user_agent AGE
AGE.IS_DELETE=0
AND AGE.AGENT_ID LIKE CONCAT('%',#{queryAgentId},'%')
and AGE.PLAN_DATE >=#{queryAgentStartTime,jdbcType=VARCHAR}
and AGE.PLAN_DATE < date_add(#{queryAgentEndTime,jdbcType=VARCHAR}, INTERVAL 1 day)
AND AGE.AGENT_NAME LIKE CONCAT('%',#{queryAgentName},'%')
AND AGE.AGENT_STATUS=#{queryAgentType,jdbcType=TINYINT}
AND AGE.IS_VIRTUAL=#{queryAgentVirtual,jdbcType=TINYINT}
ORDER BY AGE.CREATE_DATE DESC

SELECT DISTINCT(AGE.AGENT_ID), AGE.AGENT_NAME, AGE.AGENT_STATUS, AGE.CREATE_DATE, AGE.ERP_CODE, AGE.IS_DELETE, AGE.IS_VIRTUAL, AGE.JOIN_DATE, AGE.PLAN_DATE FROM user_agent AGE LEFT JOIN user_terminal TER ON AGE.AGENT_ID=TER.AGENT_ID AND TER.IS_DELETE=0 LEFT JOIN system_zone ZON ON ZON.ZONE_CODE = TER.ZONE_CODE AND ZON.IS_DELETE=0
AGE.IS_DELETE=0
AND ZON.ZONE_CODE = #{zoneCode,jdbcType=VARCHAR}
AND AGE.AGENT_ID LIKE CONCAT('%',#{queryAgentId},'%')
and AGE.PLAN_DATE >=#{queryAgentStartTime,jdbcType=VARCHAR}
and AGE.PLAN_DATE < date_add(#{queryAgentEndTime,jdbcType=VARCHAR}, INTERVAL 1 day)
AND AGE.AGENT_NAME LIKE CONCAT('%',#{queryAgentName},'%')
AND AGE.AGENT_STATUS=#{queryAgentType,jdbcType=TINYINT}
AND AGE.IS_VIRTUAL=#{queryAgentVirtual,jdbcType=TINYINT}
ORDER BY AGE.CREATE_DATE DESC

SELECT DISTINCT(AGE.AGENT_ID), AGE.AGENT_NAME, AGE.AGENT_STATUS, AGE.CREATE_DATE, AGE.ERP_CODE, AGE.IS_DELETE, AGE.IS_VIRTUAL, AGE.JOIN_DATE, AGE.PLAN_DATE FROM user_agent AGE LEFT JOIN user_terminal TER ON AGE.AGENT_ID=TER.AGENT_ID AND TER.IS_DELETE=0 LEFT JOIN system_office OFF ON OFF.OFFICE_CODE = TER.OFFICE_CODE AND OFF.IS_DELETE=0
AGE.IS_DELETE=0
and OFF.OFFICE_CODE = #{officeCode,jdbcType=VARCHAR}
AND AGE.AGENT_ID LIKE CONCAT('%',#{queryAgentId},'%')
and AGE.PLAN_DATE >=#{queryAgentStartTime,jdbcType=VARCHAR}
and AGE.PLAN_DATE < date_add(#{queryAgentEndTime,jdbcType=VARCHAR}, INTERVAL 1 day)
AND AGE.AGENT_NAME LIKE CONCAT('%',#{queryAgentName},'%')
AND AGE.AGENT_STATUS=#{queryAgentType,jdbcType=TINYINT}
AND AGE.IS_VIRTUAL=#{queryAgentVirtual,jdbcType=TINYINT}
ORDER BY AGE.CREATE_DATE DESC
