mapper.select:
SELECT TER.TERMINAL_ID, TER.TERMINAL_NAME, TER.TERMINAL_TYPE, TER.ERP_CODE, TER.TERMINAL_ADDRESS, TER.TERMINAL_STATUS, TER.IS_DELETE, TER.JOIN_DATE, TER.PLAN_DATE, TER.CREATE_DATE, TER.PRODUCT_CATEGORY_IDS, STA.STATION_ID, STA.STATION_CODE, STA.STATION_NAME, STA.IS_DELETE AS STA_ISDELETE, OFF.OFFICE_ID, OFF.OFFICE_CODE, OFF.OFFICE_NAME, OFF.IS_DELETE AS OFF_ISDELETE, ZON.ZONE_ID, ZON.ZONE_CODE, ZON.ZONE_NAME, ZON.IS_DELETE AS ZON_ISDELETE, AGE.AGENT_ID, AGE.AGENT_NAME, AGE.ERP_CODE AS AGE_ERP_CODE, AGE.IS_DELETE AS AGE_ISDELETE, CHN.CHANNEL_ID, CHN.CHANNEL_NAME, CHN.IS_DELETE AS CHN_ISDELETE, HIE.HIERARCHY_ID, HIE.HIERARCHY_NAME, HIE.HIERARCHY_NICK, HIE.IS_DELETE AS HIE_ISDELETE, PRO.AreaCode AS TERMINAL_PROVINCE, PRO.AreaName AS PROVINCE_NAME, DIS.AreaCode AS TERMINAL_DISTRICT, DIS.AreaName AS DISTRICT_NAME, CIT.AreaCode AS TERMINAL_CITY, CIT.AreaName AS CITY_NAME, SUR.USER_ID, SUR.USER_NAME, SUR.IS_ACTIVE FROM user_terminal TER LEFT JOIN user_agent AGE ON TER.AGENT_ID = AGE.AGENT_ID LEFT JOIN system_channel CHN ON TER.CHANNEL_ID = CHN.CHANNEL_ID LEFT JOIN system_station STA ON TER.STATION_CODE = STA.STATION_CODE AND STA.IS_DELETE=0 LEFT JOIN system_office OFF ON TER.OFFICE_CODE = OFF.OFFICE_CODE AND OFF.IS_DELETE=0 LEFT JOIN system_zone ZON ON TER.ZONE_CODE = ZON.ZONE_CODE AND ZON.IS_DELETE=0 LEFT JOIN system_hierarchy HIE ON TER.HIERARCHY_ID = HIE.HIERARCHY_ID LEFT JOIN security_user_station SUS ON STA.STATION_CODE = SUS.STATION_CODE LEFT JOIN security_user SUR ON SUS.USER_ID = SUR.USER_ID AND SUR.USER_ID IS NOT NULL LEFT JOIN system_area PRO ON TER.TERMINAL_PROVINCE = PRO.AreaCode LEFT JOIN system_area DIS ON TER.TERMINAL_DISTRICT = DIS.AreaCode LEFT JOIN system_area CIT ON TER.TERMINAL_CITY = CIT.AreaCode
TER.IS_DELETE = 0
and TER.TERMINAL_ID like CONCAT('%',#{queryTerminalCode},'%')
and TER.TERMINAL_NAME like CONCAT('%',#{queryTerminalName},'%')
and SUR.USER_ID=#{queryAgent,jdbcType=VARCHAR}
and TER.TERMINAL_STATUS=#{queryTerminalStatus,jdbcType=TINYINT}
and TER.TERMINAL_TYPE=#{queryTerminalType,jdbcType=TINYINT}
and TER.CHANNEL_ID=#{queryChannelId,jdbcType=VARCHAR}
and TER.HIERARCHY_ID=#{queryHierarchyId,jdbcType=VARCHAR}
ORDER BY TER.CREATE_DATE DESC

